  // Trigger the auto-build using provider, await result, then navigate
  Future<void> _runAutoBuildAndNavigate(
    BuildContext hostContext,
    String purpose,
    double budget,
  ) async {
    final notifier = ref.read(autoBuildProvider.notifier);

    try {
      await notifier.generateAutoBuild(purpose: purpose, budget: budget);

      // Success â†’ switch to Build tab
      ref.read(navIndexProvider.notifier).state = 1;
    } catch (e) {
      if (!mounted) return;
      ScaffoldMessenger.of(
        hostContext,
      ).showSnackBar(SnackBar(content: Text("AutoBuild failed: $e")));
    }
  }

  // Small reusable purpose card
  Widget _modernPurposeCard({
    required IconData icon,
    required String label,
    required bool isSelected,
    required List<Color> gradient,
    required VoidCallback onTap,
  }) {
    return Material(
      color: Colors.transparent,
      child: InkWell(
        onTap: onTap,
        borderRadius: BorderRadius.circular(14),
        child: AnimatedContainer(
          duration: const Duration(milliseconds: 200),
          curve: Curves.easeInOut,
          decoration: BoxDecoration(
            gradient: isSelected
                ? LinearGradient(
                    colors: gradient,
                    begin: Alignment.topLeft,
                    end: Alignment.bottomRight,
                  )
                : null,
            color: isSelected ? null : Colors.white,
            border: Border.all(
              color: isSelected ? Colors.transparent : const Color(0xFFE8EAFC),
              width: 2,
            ),
            borderRadius: BorderRadius.circular(14),
            boxShadow: isSelected
                ? [
                    BoxShadow(
                      color: gradient[0].withOpacity(0.35),
                      blurRadius: 12,
                      offset: const Offset(0, 4),
                    ),
                  ]
                : [
                    BoxShadow(
                      color: Colors.black.withOpacity(0.05),
                      blurRadius: 4,
                      offset: const Offset(0, 2),
                    ),
                  ],
          ),
          child: Stack(
            children: [
              Center(
                child: Row(
                  mainAxisAlignment: MainAxisAlignment.center,
                  children: [
                    Icon(
                      icon,
                      color: isSelected ? Colors.black : Colors.grey[700],
